mapper.select:
/* kr.co.bsisys.com.biz.code.dao.ComClCodeDao.selectComClCodeList */ SELECT CL_CODE , CL_CODE_NM , USE_AT FROM COM_CL_CODE WHERE 1 = 1 AND USE_AT = 'Y'
AND CL_CODE LIKE CONCAT ('%', #{searchKeyword},'%')
AND CL_CODE_NM LIKE CONCAT ('%', #{searchKeyword},'%')
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

/* kr.co.bsisys.com.biz.code.dao.ComClCodeDao.selectComClCodeListTotCnt */ SELECT COUNT(*) totcnt FROM COM_CL_CODE WHERE 1 = 1
AND CL_CODE LIKE CONCAT ('%', #{searchKeyword},'%')
AND CL_CODE_NM LIKE CONCAT ('%', #{searchKeyword},'%')

mapper.insert:
/* kr.co.bsisys.com.biz.code.dao.ComClCodeDao.insertComClCode */
INSERT 
INTO  COM_CL_CODE
 (    CL_CODE
 ,    CL_CODE_NM
 ,    CL_CODE_DC
 ,    USE_AT
 ,    CREATEDDATE
 ,    CREATEDBY_ID
 ,    LASTMODIFIEDDATE
 ,    LASTMODIFIEDBY_ID
 )
 VALUES
 (    #{clCode}
 ,    #{clCodeNm}
 ,    #{clCodeDc}
 ,    #{useAt}
 ,    sysdate()
 ,    #{createdbyId}
 ,    sysdate()
 ,    #{createdbyId}
 )

mapper.update:
/* kr.co.bsisys.com.biz.code.dao.ComClCodeDao.updateComClCode */
UPDATE COM_CL_CODE
SET    CL_CODE_NM        = #{clCodeNm}
,      CL_CODE_DC        = #{clCodeDc}
,      USE_AT            = #{useAt}
,      LASTMODIFIEDDATE  = sysdate()
,      LASTMODIFIEDBY_ID = #{lastmodifiedbyId}
WHERE  CL_CODE           = #{clCode}

mapper.delete:
/* kr.co.bsisys.com.biz.code.dao.ComClCodeDao.deleteComClCode */
UPDATE  COM_CL_CODE
SET     USE_AT  = 'N'
WHERE   CL_CODE = #{clCode}
